package Lecture1;

public class odd_even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 657;
		if(n>120) {
		System.out.println("greater");
		}
		if (n % 2 == 0) {
			System.out.println("even");
		}
		else {
			System.out.println("odd");
			
		}

	}

}
